USE `sadrzaj`;

/*Table structure for table `opis` */

DROP TABLE IF EXISTS `opis`;

CREATE TABLE `opis` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `opis1` text COLLATE utf8_unicode_ci,
  `opis2` text COLLATE utf8_unicode_ci,
  `opis3` text COLLATE utf8_unicode_ci,
  `opis4` text COLLATE utf8_unicode_ci,
  `opis5` text COLLATE utf8_unicode_ci,
  `opis6` text COLLATE utf8_unicode_ci,
  `opis7` text COLLATE utf8_unicode_ci,
  `opis8` text COLLATE utf8_unicode_ci,
  `opis9` text COLLATE utf8_unicode_ci,
  KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `opis` */

insert  into `opis`(`id`,`opis1`,`opis2`,`opis3`,`opis4`,`opis5`,`opis6`,`opis7`,`opis8`,`opis9`) values (1,'Firentinska katedrala (tal. Cattedrale di Santa Maria del Fiore ili Duomo) je stolna bazilika Firentinske nadbiskupije. Po svome značaju u povijesti arhitekture je jedna od najpoznatijih talijanskih građevina, a nalazi se u samom središtu Firence koji je zaštićen kao svjetska baština. Treća je po dužini crkva na svijetu, duga 148 metara.','Zbog ambiciozno zamišljenog plana kupole koji se dugo nije moglo ostvariti, izgradnja je završena tek 173 godine nakon početka gradnje, te je obuhvatila i gotičke i renesansne oblike. Konačno riješenje izgradnje kupole ostvario je Fillipo Brunelleschi, kojemu je to remek-djelo donijelo trajnu slavu u gradu Firenci.','Ispred katedrale nalazi se krstionica koje ima korijene još u ranokršćanskom 5. stoljeću, ali u današnjem obliku je izgrađena u Romanici (11.-12. stoljeće). Eksterijer oktogonalne građevine prekriven je, tipično romaničkim, inkrustracijama zelenog i bijelog mramora.','Sadrži troja brončana vrata, koja su remek djela. <b>Južna vrata</b> je od 1330. do 1336. izradio Andrea Pissano s 28 kvadrata koja predstavljaju scene iz života Ivana Krstitelja i alegorijske figure kardinala i teoloških vrlina. <b>Sjeverna vrata</b> 1402. godine izradio Lorenzo Ghiberti, inspirirana su postojećim južnim vratima; također sadrže 28 ploča koja predstavljaju scene iz Novog zavjeta i Djela evanđeoska. Najslavnija - <b>istočna vrata</b>, tzv. \"Rajska vrata\", su također djelo Ghibertija (1425.-1452.), ali je na njima majstor bio odvažniji u uporabi perspektive, visokog i plitkog reljefa i portreta, te se ona smatraju prvim djelom renesanse.','Gradnju katedrale započeo je Arnolfo di Cambio 1296. godine. Financirana od firentinske republike, trebala je biti veličanstveni izraz srednjovjekovne vjere, ali i ponosa građanske uprave u Italiji. Lijepa školjka, od bijelog i zelenog mramora, nad gotičkim apsidalnim tlocrtom trebala je formirati mirnu i prostranu unutrašnjost.','Tako su mnogi arhitekti kasnije proširivali i mijenjali Di Cambiov izvorni projekt. Među njima je bio i Giotto koji je sagradio odvojeni zvonik (campanil) 1334. godine. Giotto je zamislio visoki i elegantni toranj koji se naoko sužava i postaje lakši s visinom (84 metra). Kako je Giotto preminuo na samom početku gradnje, 1337. godine, njega su završili Andrea Pissano i Francesco Talenti koji su se držali genijevog nacrta.','Filippo Brunelleschi posvetio je četrnaest godina svog života (između 1420. i 1434. godine) kako bi ovoj veličanstvenoj katedrali dodao renesansnu kupolu koja još uvijek izaziva divljenje cijeloga svijeta. Golema kupola s promjerom od 42,21 metara je pravo remek djelo oblikovanja i tehnike. Kupola se nije mogla graditi sredstvima kojima su raspolagali graditelji gotičkih katedrala, te se Brunelleschi dosjetio oživljavanja starih oblika za suvremene potrebe – Renesansa.','Brunelleschi je iznad svakog stuba oktogonalnog tambura nastavio graditi vitka gotička rebra koja tvore oštri luk prema vrhu. Između svakog rebra stavio je dva unutarnja rebra koja se ne vide izvana, a tvore još strmiji zid u obliku riblje kosti kako bi konstrukcija bila jača i lakša. Između tih dvostrukih zidova nalazilo se i stubište koje je vodilo na vrhu. Vanjska obloga od crvenih pločica u kontrastu je sa zelenim i bijelim mramornim pročeljem crkve.',NULL),(2,'Galerija Uffizi (tal. Galleria degli Uffizi) se nalazi u Palazzo degli Uffizi, palača u Firenci, a predstavlja jednu od najstarijih i najslavnijih muzeja umjetnosti u svijetu. Nalazi se na koordinatama: 43°46′6.38″ N 11°15′21.24″ E. Palača Uffizi je pod jednim krovom ujedinila administrativne urede, sud i državni arhiv (Archivio di Stato).','Danas je Uffizi jedna od najpopularnijih turističkih atrakcija Firence. Na vrhuncu sezone (osobito u srpnju), čekanje u redu na ulazak može trajati i do pet sati. Posjetitelji koji su unaprijed rezervirali karte čekaju mnogo manje.','Palaču je započeo graditi Giorgio Vasari 1560. g. za Cosima Medicija kao ured Firentinskih upravitelja — odatle joj i ime \"uffizi\" (\"uredi\"). Gradnju su po Vasarijevim nacrtima nastavili Alfonso Parigi i Bernardo Buontalenti, te su je završili 1581. g. Dvorište cortile je dugo i usko, te se svojom dorskom kolonadom otvara prema rijeci Arno čime se uokviruje prostor a da se ne zatvara.','Povjesničari umjetnosti smatraju da je to najraniji primjer organizirane ulične panorame u Europi. Vasari, koji je bio i slikar i arhitekt, naglasio je perspektivnu dužinu usklađujući krovne istake pročelja s neprekinutim istakama između katova, kao i tri neprekinute stube na kojima počiva prednja fasada palače.','Palača Uffizi je pod jednim krovom ujedinila administrativne urede, sud i državni arhiv (Archivio di Stato). Cosimo I., nadvojvoda Toskane, uredio je da najbolja djela u kolekciji obitelji Medici dospiju u tzv. piano nobile (Plemeniti kat). Danas ova izvanredna remekdjela, ujedinjena s djelima iz kolekcije još jednog nadvojvode - Francesca I., čine glavnu atrakciju u muzejskom arhivu.','S vremenom, susjedni dijelovi palače su uključeni u izložbeni prostor za ostale slike i skulpture koje su naručili Medici. Nakon što je obitelj Medici nestala, djela koja su bila u njihovom vlasništvu, prema uvjetima slavnog Patto di famiglia koji je objavila Anna Maria Lodovica, posljednja baštinica obitelji Medici, jedan od prvih modernih muzeja je ustanovljen. Galerija je povremeno otvarana posjetiteljima od 16. st., a 1765. g. je službeno otvorena javnosti.','Zbog veličine kolekcije neka od djela su proslijeđena u druge Firentinske muzeje. Npr. Neke slavne skulpture se danas nalaze u muzeju Bargello. U tijeku je projekt proširenja muzejskog izložbenog prostora sa 6,000 metara² na skoro 13,000 metara², čime će se omogućiti da se vide neka djela koja su bila u skladištu.','Godine 1993. auto-bomba je eksplodirala u Via dei Georgofili i oštetila dijelove palače ubivši petoro ljudi. Najveću štetu pretrpjela je soba Niobe; te njene klasične skulpture i neoklasični interijer koji je kasnije obnovljen, ali njene freske su bile nepopravljive. Identitet bombaša nije nikad utvrđen iako.',NULL),(3,'Palazzo Pitti je renesansna palača s južne strane rijeke Arno. Potječe iz 15. stoljeća, kad je bila dom bogatog bankara Pittija, da bi je sto godina kasnije kupila obitelj Medici i pretvorila u jednu od svojih rezidencija. U 16. stoljeću prostor se udvostručuje, gradi se već spomenuti \'Corridoio Vasariano\', a kupljena je i zemlja na brdu Boboli pokraj palače, kako bi se izgradio vrt, danas poznat kao vrtovi Boboli, kojeg krase brojni drvoredi, skulpture te fontane.','Palazzo Pitti je palača s izrazitim obilježjima firentinske renesanse i čuvena galerija slika u Firenci. Dao ju je podići trgovac Luca Pitti (1394.-1472.) i građena je u drugoj polovici 15. st. Poslije palača prelazi u vlasništvo obitelji Medici i do 1859. bila je rezidencija toskanskih velikih vojvoda.','S početkom 20. stoljeća, pa sve do danas, palača postaje otvorena za javnost, a može se razgledati oko 140 unutrašnjih prostorija. Trenutačno je to najveći kompleks galerija u Firenci, od kojih izdvajamo Galeriju Palatina, Kraljevske apartmane, Galeriju moderne umjetnosti, Muzej srebra, Muzej porculana, Galeriju kostima i Muzej kočija.','Ulaznica za Palazzo Pitti košta 12 eura, a za Giardino di Boboli (s uključenim Muzejom porculana, Muzejom srebra i Galerijom kostima) 10 eura. Unutar Boboli vrtova preporučujemo Mali otok (L\'Isolotto), Neptunovu fontanu, Aleju čempresa i Veliku špilju (La Grotta Grande).',NULL,NULL,NULL,NULL,NULL),(4,'Ponte Vecchio ili \'Stari most\', srednjovjekovna je građevina koja povezuje dvije obale rijeke Arno. Sadašnji oblik potječe iz 14. stoljeća, dok prvi pisani dokumenti govore o mostu još u 10. stoljeću. Ponte Vecchio napravljen je od tri glavna luka, od kojih najveći ima raspon od 30 metara, a druga dva po 27 metara. On je ujedno i jedini most koji je \'preživio\' bombardiranja grada tijekom Drugoga svjetskog rata.','Danas je prepun zlatarnica i draguljarnica, dok su u prošlosti većinu činili mesari, koji su protjerani s izgradnjom čuvenog \'Corridoio Vasariano\', svojevrsnog \'hodnika\' koji spaja Palazzo Vecchio i Palazzo Pitti, tj. staru i novu rezidenciju obitelji Medici. Prodavaonice koje su vidljive s vanjske strane mosta dodane su u 17. stoljeću.','Jedna od starih priča kaže da je na ovom mjestu nastao i pojam stečaja, tj. bankrota. Kad trgovac više ne bi mogao platiti svoje dugove, klupa (tal. banco) na kojoj je prodavao svoja dobra, bila bi slomljena od strane gradskih vojnika, što bi se onda zvalo bancorotto, tj. slomljena klupa. Kako više ne bi imao svoj glavni alat za trgovinu, prodavač nije imao gdje poslovati, pa bi bankrotirao.','Oko Ponte Vecchija primijetit ćete brojne lokote koji \'ukrašavaju\' ograde i sigurnosne lance uz cestu. Naime, ti lokoti pripadaju zaljubljenim parovima iz cijelog svijeta koji na njih upisuju svoja imena, a nakon što ih stave na lanac, bacaju ključ u rijeku Arno i time \'zaključavaju\' svoju ljubav za sva vremena. Prava romantika.',NULL,NULL,NULL,NULL,NULL),(5,'Bazilika sv. Lovre (tal. San Lorenzo) je jedna od većih firentinskih crkava poznata po svojoj renesansnoj unutrašnjosti i po kapelama, u kojima su pokopani članovi obitelji Medici.','San Lorenzo je tlocrtno tipična trobrodna bazilika latinskog križa sa transeptom (poprečni brod). U transeptu bazilike se sa lijeve i desne strane nalaze; Stara i Nova sakristija. U nastavku glavnog broda bazilike je kasnije dograđena Kapela vladara s oltarima, koja po svojim dimenzijama značajno odskače od ostalih prostora crkve, njena kupola nadvisuje sve ostale dijelove bazilike. Uz Baziliku San Lorenzo je samostan u kojem se nalazi knjižnica Medici-Laurenziana. Kasnije dogradnje - Nova sakristija i knjižnica Medici-Laurenziana napravljene su po projektu Michelangela.','Na mjestu San Lorenza, stajala je starija crkva iz 4. stoljeća koja je kompletno obnovljena u romaničkom stilu u 11. stoljeću, i posvećena 1059., nju su firentinci 1418. odlučili povećati i obnoviti po projektu tada mladog arhitekta Filippa Brunelleschija. Financijer je bio Giovanni di Bicci de\' Medici. Taj novi prostor trebao je postati mjesto za pokop Medicejaca - kasnije je nazvan Stara sakristija.','Brunelleschi je nacrt napravio 1421. Gradnja je započela 1422. ali se preuređenje odužilo zbog ratova i nedostatka novca, nakon sedemnajst godina je Cosimo de\' Medici preuzeo troškove izgradnje. 1446. je Brunelleschi umro, tako da je gradnju nastavio Antonio Manetti i dovršio 1448. Po Michelangelovom projektu su portal i galerija za smještaj relikvija koje je papa Klement VII. poklonio crkvi, izvedeni 1532. godine u unutrašnjosti na ulazu u baziliku. Uz crkvu je podignut zvonik 1741. po projektu Ferdinanda Ruggierija.','Obnova bazilike San Lorenzo započela je izgradnom Stare sakristije. To je zapravo kapela, jednostavnog tlocrta u obliku kocke, koje se u svom gornjem dijelu preko pandativa nastavlja u rebrastu kupolu. Brunelleschi je upotrijebio iste elemente, kao kod predgradnje glavnog broda, kombinirao je kontrast između ukrasne plastike isklesane iz tamnog plavo-sivog mramora i bjeline zidova.','Kiparsku plastiku izveo je Donatello, ukrasni gornji vijenac formiraju kerubini, u pendativ je smjestio višebojne okrugle reljefe s prizorima iz života sv. Ivana Evangelista. Izveo je oltar sa čipkastom kamenom ogradom, brončana vrata sa obje strane oltara sa reljefima četrdeset mučenika i apostola iznad kojih su reljefi sv. Kuzme i sv. Damjana i sv. Stjepana i sv. Lovre.','U središtu kapele leži sarkofag u koji je pokopan - Giovanni Bicci Medici i njegove žene Picarde Bueri. Lijevo od ulaza leži elegantna grobnica Piera i njegova brata Giovannija koju je po narudžbi Lorenza Veličanstvenog isklesao Andrea Verrocchio 1472.','Kardinal Giulio di Giuliano de\' Medici je 1520. naručio projekt od Michelangela za izgradnju Nove sakristije. Michelangelo je u tlocrtu - slijedio u osnovi koncept Brunelleschijeve Stare sakristije, ali je pod kasetiranu kupolu ugradio i mezanin i tako dobio daleko višu prostoriju. Čitav prostor je harmonično podijelio na tamne i svijetle dijelove.','Grobove je postavio na tri zida, na zidu nasuprot oltara su grobnice Lorenza Veličanstvenog i njegovog ubijenog brata Gulijana, a na bočnim zidivima grobovi Lorenzovog najmlađeg sina, - Giulijana Vojvode od Nemoursa i unuka Lorenza II. Vojvode od Urbina. Te dvije bočne grobnice je Michelangelo napravio sa sjedećim figurama pokojnika, ispod kojih su po dvije ležeće alegorične figure. Nakon 13 godina rada na tom projektu (1521. - 1534.), Michelangelo je napustio Firencu, tako da je ta kapela ostala nedovršena, kao i grobovi Lorenza i Gulijana koji su trebali dobiti puno više kipova. Ipak je uspio dovršiti kip Madonne Medici za oltar kapele.'),(6,'Bazilika Svetog Krsta (ital.: Basilica Santa Croce) je franjevačka crkva u Firenci i manja bazilika rimokatoličke crkve. Nalazi se na trgu Svetog Krsta (Piazza Santa Croce), istočno od Duoma.','Bazilika je najveća franjevačka crkva na svijetu. Najznačajnija obilježja su šesnaest kapelice, mnoge od njih ukrašene su freskama Giotta i njegovih učenika. Legenda kaže da je bazilika osnovana od strane samog svetog Franje.Gradnja današnje crkve, koja bi zamijenila starije zgrade, je započela 12. svibnja 1294, vjerojatno Arnolfo di Cambio. Posvetio ju je 1442 papa Eugen IV. Tlocrt je egipatski ili Tau križ (simbol svetog Franje), 115 metara u dužinu s tri broda odvojena linijama osmerokutnih stupaca. Na južnoj strani crkve je samostan.','Zvonik je sagrađen 1842, tako što je zamijenjen stari zvonik koji je oštećen od udara groma.Neogotički mramorna fasadu izradio je Nicolo Matas 1857-1863.','Židovski arhitekt Niccolo Matas iz Ancone, dizajnirao je u 19. stoljeću neo-gotičku fasadu Crkve, istaknuvši Davidovu zvijezda. Matas je želio biti pokopan sa svojim vršnjacima, ali pošto je bio Židov, pokopan je ispod trijema i nije unutar zidina.','U crkvi su sahranjene mnoge poznate ličnosti: Leon Battista Alberti, Leonardo Bruni, Niccolò Machiavelli, Michelangelo Buonarroti, Galileo Galilej, Enrico Fermi, Dante, Raffaello Morghen.',NULL,NULL,NULL,NULL),(7,'Firenca (tal. Firenze) je glavni grad pokrajine Toskana u Italiji. Grad leži na rijeci Arno i najpoznatiji je po svojoj srednjovjekovnoj povijesti grada-države Firence, u vrijeme kada je bila središte trgovine i financija te jedan od najbogatijih gradova u Europi, a posebice kao kolijevka talijanske renesanse, o čemu svjedoče brojni gradski spomenici.','Godine 1982., UNESCO je uvrstio stari povijesni centar na popis svjetske baštine. Svojom arhitekturom, ali i muzejima (Pitti, Uffizi), vrtovima, obrazovanjem, glazbom, kuhinjom, modom i dr., Firenca svake godine privlači oko milijun turista.',NULL,NULL,NULL,NULL,NULL,NULL,NULL);

/*Table structure for table `znamenitosti` */

DROP TABLE IF EXISTS `znamenitosti`;

CREATE TABLE `znamenitosti` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `naziv` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  CONSTRAINT `znamenitosti_ibfk_1` FOREIGN KEY (`id`) REFERENCES `opis` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `znamenitosti` */

insert  into `znamenitosti`(`id`,`naziv`) values (1,'Firentinska katedrala'),(2,'Galerija Uffizi'),(3,'Palazzo Pitti'),(4,'Ponte Vecchio'),(5,'Bazilika San Lorenzo'),(6,'Bazilika Svetog Krsta'),(7,'Pocetna');

